mapper.update:
UPDATE elective
SET
    score = #{score}
WHERE id = #{id}

UPDATE elective
SET
    score = #{score}
WHERE id = #{id}

mapper.select:
SELECT elective.id e_id, student_id, teaching_id, score,
student.id s_id, student.name s_name, student.sex s_sex, student.number s_number, profession,
teaching.id ting_id, teaching.semester, teaching.state, teacher_id, course_id,
teacher.id t_id, teacher.name t_name, teacher.sex t_sex, teacher.number t_number,
course.id c_id, course.name c_name, course.classhour, course.number c_number FROM elective LEFT OUTER JOIN teaching ON elective.teaching_id = teaching.id LEFT OUTER JOIN student ON elective.student_id = student.id LEFT OUTER JOIN teacher ON teaching.teacher_id = teacher.id LEFT OUTER JOIN course ON teaching.course_id = course.id WHERE 1=1
AND teacher.name = #{teacherName}
AND course.name = #{courseName}
AND student.name = #{studentName}
ORDER BY (course.name + 0), (student.name + 0), (teacher.name + 0)
