mapper.update:
UPDATE equipment_lease
SET
    return_time = #{returnTime},
    state = #{state}
WHERE id = #{id}

mapper.select:
SELECT equipment_lease.*, student.id s_id, student.name s_name, student.number s_number, equipment.id e_id, equipment.name e_name FROM equipment_lease LEFT OUTER JOIN student ON equipment_lease.student_id = student.id LEFT OUTER JOIN equipment ON equipment_lease.equipment_id = equipment.id WHERE 1=1
AND equipment.name like "%"#{equipmentName}"%"
AND student.number = #{studentNumber}
