mapper.update:
UPDATE field_lease
SET expire = #{expire}
WHERE id = #{id}

mapper.select:
SELECT field_lease.*, student.id s_id, student.name s_name, student.number, field.id f_id, field.name f_name, field.state FROM field_lease LEFT OUTER JOIN student ON field_lease.student_id = student.id LEFT OUTER JOIN field ON field_lease.field_id = field.id WHERE 1=1
AND field.name like "%"#{fieldName}"%"
AND student.number = #{studentNumber}
