mapper.update:
UPDATE teaching
SET state = #{state}
WHERE id = #{id}

mapper.select:
SELECT teaching.*, teacher.id tid, teacher.name tname, teacher.sex, teacher.number tnumber, course.id cid, course.name cname, course.classhour, course.number cnumber FROM teaching LEFT OUTER JOIN teacher ON teaching.teacher_id = teacher.id LEFT OUTER JOIN course ON teaching.course_id = course.id WHERE 1=1
AND teacher.name = #{teacherName}
AND course.name = #{courseName}
